// Get the error data from the Error Trigger node's input.
const errorData = $input.item.json;

// Extract error information
const workflowId = errorData.workflow?.id || 'unknown';
const workflowName = errorData.workflow?.name || 'Unknown Workflow';
const failedNode = errorData.lastNodeExecuted?.name || 'Unknown Node';
const errorMessage = errorData.execution?.error?.message || 'An unknown error occurred.';
const executionId = errorData.execution?.id || 'unknown';
const executionUrl = errorData.execution?.url || '';

// Prepare data for HTTP Request and Email fallback
return [{
  json: {
    workflow_id: workflowId,
    workflow_name: workflowName,
    failed_node: failedNode,
    error_message: errorMessage,
    execution_id: executionId,
    execution_url: executionUrl,
    // Email fallback data - UPDATE THIS EMAIL
    email: 'your-email@example.com',
    failedWorkflow: workflowName,
    failedNode: failedNode,
  }
}];
